import React, { Fragment } from "react";

class Month extends React.Component {
	constructor(props){
		super(props);

		this.state = {
			isShown: false
		} 

	}

	getColor(usersCount) {
		if(usersCount <= 2)
			return "gray";
		else if(usersCount <= 6)
			return "blue";
		else if(usersCount <= 10)
			return "green";
		else
			return "red";

	}

	render() {
		let { monthName, users } = this.props;
		return (
			<>
				<h1 className="month" style={{ color: this.getColor(users.length) }}>{ monthName }</h1>
				<div className="listUsers">
					{ users.map(user => (
                    	<p key={ user.id} >{user.firstName + " " + user.lastName}</p>
                    ))}
				</div>
			</>
		)	
	}
}


export default Month;
